<button data-help-text="Step over" data-keybinding-id="step_over" data-keybinding-default="~s" class="btn no-margin-top" data-command="step_over"><span class="fa fa-fw fa-step-forward"></span></button>
<button data-help-text="Step into" data-keybinding-id="step_into" data-keybinding-default="~d" class="btn no-margin-top" data-command="step_into"><span class="fa fa-fw fa-long-arrow-alt-down"></span></button>
<button data-help-text="Step out" data-keybinding-id="step_out" data-keybinding-default="~f" class="btn no-margin-top" data-command="step_out"><span class="fa fa-fw fa-long-arrow-alt-up"></span></button>
<button data-help-text="Run" data-keybinding-id="run" data-keybinding-default="~j" class="btn no-margin-top" data-command="run"><span class="fa fa-fw fa-play"></span></button>
<button data-help-text="Stop" data-keybinding-id="stop" data-keybinding-default="~k"class="btn no-margin-top" data-command="stop"><span class="fa fa-fw fa-stop"></span></button>
<button data-help-text="Detach" data-keybinding-id="datch" data-keybinding-default="~l" class="btn no-margin-top" data-command="detach"><span class="fa fa-fw fa-unlink"></span></button>
